package org.mj.process.tools;

import org.apache.commons.codec.DecoderException;
import org.apache.commons.codec.binary.Hex;

import java.io.UnsupportedEncodingException;

public class HexTools {

    public static void main(String[] args) throws DecoderException, UnsupportedEncodingException {
        System.out.println(decode(""));
    }

    public static String decode(String hex) throws DecoderException, UnsupportedEncodingException {
        byte[] bytes = Hex.decodeHex(hex.toCharArray());
        return new String(bytes, "UTF-8");
    }
}
